import './DragDrop.css';
import { fabric } from 'fabric';

import React, { Component } from 'react';

export default class DragDrop extends Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
    this.canvas = '';
  }
  state = {
    selectedImg: ''
  };
  onDragStart = event => {
    console.log('L16 onDragStart', event.target);
    this.setState({ selectedImg: event.target });
  };
  drawImage = () => {
    // let img = new Image();
    // let fabricCopy = fabric;
    let canvasCopy = this.canvas;
    // img.onload = function() {
    //   var fabricImg = new fabricCopy.Image(img);
    //   canvasCopy.setBackgroundImage(fabricImg);
    //   canvasCopy.renderAll();
    // };
    // img.src = this.state.selectedImg.src;
    fabric.Image.fromURL(this.state.selectedImg.src, function(img) {
      console.log(canvasCopy.width);
      img
        .set({
          width: canvasCopy.width,
          height: canvasCopy.height,
          left: 0,
          top: 0
        })
        .scale(1);
      canvasCopy.add(img).renderAll();
      // .setActiveObject(img);
    });
  };

  componentDidMount() {
    console.log('L 33 CanvasRef-->', this.canvasRef.current);
    this.canvas = new fabric.Canvas(this.canvasRef.current);
    this.canvas.on('drop', this.drawImage);
  }
  render() {
    return (
      <div>
        <div className="container">
          <div className="left">
            <div className="content">
              <img
                draggable
                src="/assets/image/1.jpg"
                alt="alt text1"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/2.jpg"
                alt="alt text2"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/3.jpg"
                alt="alt text3"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/4.jpg"
                alt="alt text4"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/5.png"
                alt="alt text5"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/6.jpg"
                alt="alt text6"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/7.jpg"
                alt="alt text7"
                onDragStart={this.onDragStart}
              />
            </div>
            <div className="content">
              <img
                draggable
                src="/assets/image/8.jpg"
                alt="alt text8"
                onDragStart={this.onDragStart}
              />
            </div>
          </div>

          <div className="right">
            <div>
              <canvas ref={this.canvasRef} className="canvas" />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
