import './DragDrop.css';
import { fabric } from 'fabric';

import React, { Component } from 'react';

export default class DragDrop extends Component {
  constructor(props) {
    super(props);
    this.canvas = '';
  }
  state = {
    selectedImg: ''
  };
  onDragStart = event => {
    console.log(event.target);
    this.setState({ selectedImg: event.target });
  };
  drawImage = () => {};

  componentDidMount() {
    this.canvas = new fabric.Canvas();
  }
  render() {
    return (
      <div>
        <div className="container">
          <div className="left">
            <div className="content">
              <img
                draggable
                src="/assets/image/1.jpg"
                alt="alt text1"
                onDragStart={e => console.log('drag start', e)}
              />
            </div>
            <div className="content">
              <img draggable src="/assets/image/2.jpg" alt="alt text2" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/3.jpg" alt="alt text3" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/4.jpg" alt="alt text4" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/5.png" alt="alt text5" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/6.jpg" alt="alt text6" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/7.jpg" alt="alt text7" />
            </div>
            <div className="content">
              <img draggable src="/assets/image/8.jpg" alt="alt text8" />
            </div>
          </div>

          <div className="right">
            <div>
              <canvas />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
